def xmlSlurper = new XmlSlurper().parseText(xml)
assert xmlSlurper instanceof groovy.util.slurpersupport.GPathResult

assert  xmlSlurper.Body.RailConnect_ScheduleQuoteRS.Success.Product[0].@Class=='001'

